mapper.select:
select u.user_id as userId, u.code, u.username, u.nickname, u.email, u.phone, u.type, u.avatar, u.description, u.captcha_status as captchaStatus, u.reset_status as resetStatus, u.org_id as orgId, o.name as orgName, u.creater_id as createrId, a.username as createrName, b.roleName, u.status, u.create_time as createTime, u.update_time as updateTime from user u left join org o on u.org_id = o.org_id left join (select user_id, username from user) a on u.creater_id = a.user_id left join (select ur.user_id, GROUP_CONCAT(r.name) as roleName from role r, user_role ur where r.role_id = ur.role_id group by ur.user_id) b on u.user_id = b.user_id where 1 = 1
and u.username like CONCAT('%',#{username},'%')
and u.nickname like CONCAT('%',#{nickname},'%')
and u.phone like CONCAT('%',#{phone},'%')
and u.email like CONCAT('%',#{email},'%')
and u.type = #{type}
and u.org_id = #{orgId}
and u.creater_id = #{createrId}
and u.status = #{status}
and u.status != #{notStatus}
and b.roleName like CONCAT('%',#{roleName},'%')
order by u.create_time desc
